var ee = require('vflow-pouchdb');
var pouchdb = require('pouchdb');
var users = pouchdb('ifile_users');

module.exports = function() {
  ee.on('session:create', function(doc) {
    // everyone can login
    
    var e = {
      verb: 'created',
      object: doc.object
    };
    ee.emit('session:created', e);
  });
};